import React from "react";

const Form = props => (
  <form className="login">
    <h3>Register</h3>
    <input
      name="reguser"
      placeholder="Username"
      className="inputField ui transparent input"
      onChange={props.onChangeHandler}
    />
    <input
      name="regpw"
      type="password"
      placeholder="Password"
      className="inputField ui transparent input"
      onChange={props.onChangeHandler}
    />
    <button className="loginButton ui button" onClick={props.regHandler}>
      {" "}
      Register{" "}
    </button>

    <h3>Login</h3>
    <input
      name="username"
      placeholder="Username"
      className="inputField ui transparent input"
      onChange={props.onChangeHandler}
    />
    <input
      name="password"
      type="password"
      placeholder="Password"
      className="inputField ui transparent input"
      onChange={props.onChangeHandler}
    />
    <button className="loginButton ui button" onClick={props.loginHandler}>
      {" "}
      Login{" "}
    </button>
  </form>
);

export default Form;
